static int default_count = 1, count = 0;
char *names[16] = {
	0,
};
int rtapi_app_main(void)
{
	int r = 0;
	int i;
	comp_id = hal_init("pokeys");
	if (comp_id < 0)
		return comp_id;
	if (count && names[0])
	{
		rtapi_print_msg(RTAPI_MSG_ERR, "count= and names= are mutually exclusive\n");
		return -EINVAL;
	}
	if (!count && !names[0])
		count = default_count;
	if (count)
	{
		for (i = 0; i < count; i++)
		{
			char buf[HAL_NAME_LEN + 1];
			rtapi_snprintf(buf, sizeof(buf), "pokeys.%d", i);
			r = export(buf, i);
			if (r != 0)
				break;
		}
	}
	else
	{
		int max_names = sizeof(names) / sizeof(names[0]);
		for (i = 0; (i < max_names) && names[i]; i++)
		{
			if (strlen(names[i]) < 1)
			{
				rtapi_print_msg(RTAPI_MSG_ERR, "names[%d] is invalid (empty string)\n", i);
				r = -EINVAL;
				break;
			}
			r = export(names[i], i);
			if (r != 0)
				break;
		}
	}
	if (r)
	{
		hal_exit(comp_id);
	}
	else
	{
		hal_ready(comp_id);
	}
	return r;
}

void rtapi_app_exit(void)
{
	hal_exit(comp_id);
}
static void user_mainloop(void);

int __comp_parse_count(int *argc, char **argv)
{
	int i;
	for (i = 0; i < *argc; i++)
	{
		if (strncmp(argv[i], "count=", 6) == 0)
		{
			errno = 0;
			count = strtoul(&argv[i][6], NULL, 0);
			for (; i + 1 < *argc; i++)
			{
				argv[i] = argv[i + 1];
			}
			argv[i] = NULL;
			(*argc)--;
			if (errno == 0)
			{
				return 1;
			}
		}
	}
	return 0;
}

int __comp_parse_names(int *argc, char **argv)
{
	int i;
	for (i = 0; i < *argc; i++)
	{
		if (strncmp(argv[i], "names=", 6) == 0)
		{
			char *p = &argv[i][6];
			int j;
			for (; i + 1 < *argc; i++)
			{
				argv[i] = argv[i + 1];
			}
			argv[i] = NULL;
			(*argc)--;
			for (j = 0; j < 16; j++)
			{
				names[j] = strtok(p, ",");
				p = NULL;
				if (names[j] == NULL)
				{
					return 1;
				}
			}
			return 1;
		}
	}
	return 0;
}

int argc = 0;
char **argv = 0;
int main(int argc_, char **argv_)
{
	argc = argc_;
	argv = argv_;
	int found_count, found_names;
	found_count = __comp_parse_count(&argc, argv);
	found_names = __comp_parse_names(&argc, argv);
	if (found_count && found_names)
	{
		rtapi_print_msg(RTAPI_MSG_ERR, "count= and names= are mutually exclusive\n");
		return 1;
	}

	if (rtapi_app_main() < 0)
		return 1;
	user_mainloop();
	rtapi_app_exit();
	return 0;
}

