bool Get_BitOfByte(uint8_t in_Byte, int Bit_Id)
{
	return (in_Byte >> Bit_Id) & 0x01;
}

uint8_t Set_BitOfByte(uint8_t in_Byte, int Bit_Id, bool value)
{

	if (value == true)
	{
		in_Byte |= 1 << Bit_Id;
	}
	else
	{
		in_Byte &= ~(1 << Bit_Id);
	}
	return in_Byte;
}

uint8_t Merge_8BitsToByte(bool Bit_array[8])
{
	uint8_t sum = 0;
	for (int i = 0; i < 8; i++)
	{
		//    sum += Bit_array[i] - '0';
		//    sum<<=1;

		if (Bit_array[i] == true)
		{
			sum |= 1 << i;
		}
		else
		{
			sum &= ~(1 << i);
		}
	}
	return sum;
}



